package exo2_algo;

public class exo4_algo {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = -9;
		if (x < 0) {
			for (; x < 0; x++) {
				System.out.println(x);
			}
		} else if (x > 0) {
			for (; x > 0; x--) {
				System.out.println(x);
			}
		} else {
			System.out.println(" ok !!! ");
		}

	}

}
